import React, { useState, useRef, useEffect } from 'react';
import axios from 'axios';
import {
    Button,
    Container,
    Text,
    Title,
    Modal,
    TextInput,
    Group,
    Card,
    ActionIcon,
} from '@mantine/core';
import { MoonStars, Sun, Trash, Edit } from 'tabler-icons-react';

import {
    MantineProvider,
    ColorSchemeProvider,
} from '@mantine/core';
import { useColorScheme } from '@mantine/hooks';
import { useHotkeys, useLocalStorage } from '@mantine/hooks';

export default function App() {
    const [tasks, setTasks] = useState([]);
    const [opened, setOpened] = useState(false);
    const [editingTask, setEditingTask] = useState(null); // State to track the task being edited
    const [currentTaskId, setCurrentTaskId] = useState(null);// State to track the task being edited

    const preferredColorScheme = useColorScheme();
    const [colorScheme, setColorScheme] = useLocalStorage({
        key: 'mantine-color-scheme',
        defaultValue: 'light',
        getInitialValueInEffect: true,
    });
    const toggleColorScheme = value =>
        setColorScheme(value || (colorScheme === 'dark' ? 'light' : 'dark'));

    useHotkeys([['mod+J', () => toggleColorScheme()]]);

    const taskTitle = useRef('');
    const taskSummary = useRef('');

    function createTask() {
        // Créez un objet de tâche à envoyer à l'API Gateway
        const newTask = {
            title: taskTitle.current.value,
            summary: taskSummary.current.value,
        };

        // Faites une requête POST à votre API Gateway pour créer une nouvelle tâche
        axios.post('https://rdc83wu5s2.execute-api.us-east-1.amazonaws.com/tasks/', newTask)
            .then(response => {
                // Mettez à jour l'état des tâches avec la nouvelle tâche créée par l'API
                setTasks([...tasks, response.data.item]);

                // Fermez la fenêtre modale une fois la tâche créée
                setOpened(false);
            })
            .catch(error => {
                console.error(error);
                // Gérez les erreurs de requête ici
            });
    }

    function deleteTask(index) {
        const clonedTasks = [...tasks];
        clonedTasks.splice(index, 1);
        setTasks(clonedTasks);
        saveTasks(clonedTasks);
    }

    function loadTasks() {
        const loadedTasks = localStorage.getItem('tasks');
        const parsedTasks = JSON.parse(loadedTasks);
        if (parsedTasks) {
            setTasks(parsedTasks);
        }
    }

    function saveTasks(tasks) {
        localStorage.setItem('tasks', JSON.stringify(tasks));
    }

    useEffect(() => {
        loadTasks();
    }, []);
    function updateTask() {
        const updatedTasks = tasks.map((task, index) => {
            if (index === editingTask) {
                return {
                    ...task,
                    title: taskTitle.current.value,
                    summary: taskSummary.current.value,
                };
            }
            return task;
        });

        setTasks(updatedTasks);
        setEditingTask(null);
        setCurrentTaskId(null);
        saveTasks(updatedTasks);
    }

    function handleEditTask(index) {
        setOpened(true);
        setEditingTask(index);
        setCurrentTaskId(tasks[index].id);
        taskTitle.current.value = tasks[index].title;
        taskSummary.current.value = tasks[index].summary;
    }
    return (
        <ColorSchemeProvider
            colorScheme={colorScheme}
            toggleColorScheme={toggleColorScheme}>
            <MantineProvider
                theme={{ colorScheme, defaultRadius: 'md' }}
                withGlobalStyles
                withNormalizeCSS>
                <div className='App'>
                <Modal
            opened={opened}
            size={'md'}
            title={editingTask !== null ? 'Edit Task' : 'New Task'}
            withCloseButton={false}
            onClose={() => {
                setOpened(false);
                setEditingTask(null);
                setCurrentTaskId(null);
            }}
            centered>
            <TextInput
                mt={'md'}
                ref={taskTitle}
                placeholder={'Task Title'}
                required
                label={'Title'}
                defaultValue={editingTask !== null ? tasks[editingTask].title : ''}
            />
            <TextInput
                ref={taskSummary}
                mt={'md'}
                placeholder={'Task Summary'}
                label={'Summary'}
                defaultValue={editingTask !== null ? tasks[editingTask].summary : ''}
            />
            <Group mt={'md'} position={'apart'}>
                <Button
                    onClick={() => {
                        setOpened(false);
                        setEditingTask(null);
                        setCurrentTaskId(null);
                    }}
                    variant={'subtle'}>
                    Cancel
                </Button>
                <Button
                    onClick={() => {
                        if (editingTask !== null) {
                            updateTask();
                        } else {
                            createTask();
                        }
                        setOpened(false);
                    }}>
                    {editingTask !== null ? 'Save Changes' : 'Create Task'}
                </Button>
            </Group>
        </Modal>
                    <Container size={550} my={40}>
                        <Group position={'apart'}>
                            <Title
                                sx={theme => ({
                                    fontFamily: `Greycliff CF, ${theme.fontFamily}`,
                                    fontWeight: 900,
                                })}>
                                My Tasks
                            </Title>
                            <ActionIcon
                                color={'blue'}
                                onClick={() => toggleColorScheme()}
                                size='lg'>
                                {colorScheme === 'dark' ? (
                                    <Sun size={16} />
                                ) : (
                                    <MoonStars size={16} />
                                )}
                            </ActionIcon>
                        </Group>
                        {tasks.length > 0 ? (
            tasks.map((task, index) => {
                if (task.title) {
                    return (
                        <Card withBorder key={task.id}> {/* Assurez-vous d'utiliser un ID unique pour chaque tâche */}
                            <Group position={'apart'}>
                                <Text weight={'bold'}>{task.title}</Text>
                                <Group>
                                    <Button
                                        variant="outline"
                                        onClick={() => handleEditTask(index)}
                                        leftIcon={<Edit />}
                                    >
                                        Edit
                                    </Button>
                                    <ActionIcon
                                        onClick={() => {
                                            deleteTask(index);
                                        }}
                                        color={'red'}
                                        variant={'transparent'}
                                    >
                                        <Trash />
                                    </ActionIcon>
                                </Group>
                            </Group>
                            <Text color={'dimmed'} size={'md'} mt={'sm'}>
                                {task.summary ? task.summary : 'No summary was provided for this task'}
                            </Text>
                        </Card>
                                    );
                                }
                            })
                        ) : (
                            <Text size={'lg'} mt={'md'} color={'dimmed'}>
                                You have no tasks
                            </Text>
                        )}
                        <Button
                            onClick={() => {
                                setOpened(true);
                            }}
                            fullWidth
                            mt={'md'}>
                            New Task
                        </Button>
                    </Container>
                </div>
            </MantineProvider>
        </ColorSchemeProvider>
    );
}
